import React, { Component } from 'react';
import { Container, Button, Jumbotron } from 'react-bootstrap';
import './Home.css';
import firebaseApp from 'firebase/app';
import 'firebase/auth';
import * as AWS from 'aws-sdk';



export default class Home extends Component {


    render() {
        AWS.config.update({
            region: 'us-east-2',
            endpoint: 'dynamodb.us-east-2.amazonaws.com',
            accessKeyId: 'AKIAYPNA3BUHUHWK52FL',
            secretAccessKey: '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'
          });

          this.dynamodb = new AWS.DynamoDB();
          this.docClient = new AWS.DynamoDB.DocumentClient();
          var table = "UsersTable";

          var name = 5433;
          
          var params = {
              TableName: table,
              Key:{
                  "user_id": name
              }
          };
          
          this.docClient.get(params, function(err, data) {
              if (err) {
                  console.error("Unable to read item. Error JSON:", JSON.stringify(err, null, 2));
              } else {
                  console.log("GetItem succeeded:", JSON.stringify(data, null, 2));
              }
          });

        //firebaseApp.auth().setPersistence(firebaseApp.auth.Auth.Persistence.SESSION)
        const user = firebaseApp.auth().currentUser;
        if (user) {
            return (
                <Container >
                    <Jumbotron>
                        <h2>Welcome to Cloud and Back Personal Tracker! %user</h2>
                        <p>Here you will be able to monitor you different monitoring devices.</p>
                    </Jumbotron>
                    <Button onClick={() => this.props.history.push('/About')}>About</Button>
                </Container >
            )
        }
        else {
            return (
                <Container >
                    <Jumbotron>
                        <h2>You are not meant to be here!</h2>
                        <p>Here you will be monitored.</p>
                    </Jumbotron>
                    <Button onClick={() => this.props.history.push('/')}>Login</Button>
                </Container >
            )
        }

    }
}
