import React, { Component } from 'react';
import { Container, Button, Jumbotron } from 'react-bootstrap';
import './Home.css';
import firebaseApp from 'firebase/app';
import 'firebase/auth';
import * as AWS from 'aws-sdk';
import PostData from './post.json'



export default class Home extends Component {
    constructor() {
        super();
        this.state = { Email: 'Need To Change' };
      }

      
      pull_data = async() =>{
        AWS.config.update({
            region: 'us-east-2',
            endpoint: 'dynamodb.us-east-2.amazonaws.com',
            accessKeyId: 'AKIAYPNA3BUHUHWK52FL',
            secretAccessKey: '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'
        });
  
        this.dynamodb = new AWS.DynamoDB();
        this.docClient = new AWS.DynamoDB.DocumentClient();
  
        var params = {
            TableName: 'users',
            Key:{
                "user_id": 'EbGp7qwQWlfaRz2PD0X0iSL0ivl2'
            }
        };
          
        var documentClient = new AWS.DynamoDB.DocumentClient();

         await documentClient.get(params, function(err, data) {
            if (err){ 
                return "YOU F'ed Up"
            }
            else{
                console.log(data.Item.email);
                this.setState({Email: data.Item.email});
 //               return data.Item;
            }
          }).promise;
      }

    render() {


        //firebaseApp.auth().setPersistence(firebaseApp.auth.Auth.Persistence.SESSION)
        const user = firebaseApp.auth().currentUser;
        const dataReturned = this.state.Email;
        console.log(dataReturned);
        this.pull_data();
        const dataReturned2 = this.state.Email;
        console.log(dataReturned2);
       
        if (user) {
           
              return (
                <div>
                 I just need this to work {dataReturned} sigh..
                  </div>);
        }
        else {
            return (
                <Container >
                    <Jumbotron>
                        <h2>You are not meant to be here!</h2>
                        <p>Here you will be monitored.</p>
                    </Jumbotron>
                    <Button onClick={() => this.props.history.push('/')}>Login</Button>
                </Container >
            )
        }

    }
}
